import * as axios from "axios";

const baseURL ='https://social-network.samuraijs.com/api/1.0/'
const API_KEY = 'f96dbdbd-76d2-4f49-b850-bf9f7065d50b'

const instance = axios.create()

export const getUsers = (pageNumber, pageSize) => {

    return axios.get( baseURL + `users?page=${pageNumber}&count=${pageSize}`,
        {withCredentials: true}).then(response => response.data)
}


export const getProfile = (userID) => {
  return  axios.get( baseURL+`profile/${userID}`).then(response => {
        return response.data})
    }

export const getUserData = () => {
   return axios.get(baseURL + `auth/me`, {withCredentials: true})
        .then(response => response.data)
}

export const setFollow = (id) => {
    return axios.post(baseURL +`follow/${id}`, {},
        {
            withCredentials: true,
            headers: {"API-KEY": API_KEY}
        })
        .then(response => response.data)
}

export const setUnfollow = (id) => {
    return  axios.delete(baseURL + `follow/${id}`, {
        withCredentials: true,
        headers: {"API-KEY": API_KEY}
    }).then(response => response.data)

}
