import React, {useState} from 'react'
import st from './Profile.module.css'
import userLogo from './../../../../src/image/userlogo.png'
import ProfileStatus from "../../Users/ProfileStatus";
import ProfileStatusWithHooks from "../../Users/ProfileStatusWithHooks";


const Profile =(props) => {

    let [editMode, setEditMode] = useState(false)
    let [possibilityToChangeFile, setPossibilityToChangeFile] = useState(props.status)


    useEffect(() => {
        setEditMode(status)
    }, [editMode])

    const onChangeMainPhoto = (e) => {
  props.changeProfilePhotoOnServer(e.target.files[0])
    }

        return (
            <div className={st.content}>
                <div className={st.contentLeft}>
                    <div className={st.item}>
                        <img onMouseOver={() => setEditMode(true)}
                             onMouseOut={() => setEditMode(false)}
                            src={(props.userInfo.photos.small === null ? userLogo : props.userInfo.photos.small)}
                            alt='user-logo'/>
                    </div>
                    <div> {(possibilityToChangeFile)&& <input type={"file"} onChange={onChangeMainPhoto}/>}</div>
                    <div className={st.nameItem}>
                        <p>{props.userInfo.fullName}</p>
                    </div>
                </div>
                <div className={st.contentRight}>
                    <div className={st.status}>
                        <ProfileStatusWithHooks isOwnPage={props.isOwnPage} status={props.status} updateStatus={props.updateStatus}/>
                    </div>
                    <div className={st.statusPersonal}>
                        <h>About personality:{props.userInfo.aboutMe}</h>
                    </div>
                    <div className={st.statusJob}>
                        <text>About job: {props.userInfo.lookingForAJobDescription} </text>
                    </div>

                </div>
            </div>

        )


}


export default Profile
