            date: '26.06.20',
            like: '8'
        }
    ],
    newPostText: 'enter here',
}

 const contentReducer = (state = initialState, action) => {

    switch (action.type) {
        case ADD_POST: {
           let stateCopy = {...state}
           stateCopy.myPosts = [...state.myPosts]
            stateCopy.myPosts.push({id: '4', from: 'Elena', message: state.newPostText, date: '25.06.20', like: '0'})
            stateCopy.newPostText = ''
            return stateCopy
          break;}
       case ADD_NEW_POST_TEXT: {
           let stateCopy = {...state}
           stateCopy.newPostText = action.newText
           return stateCopy
          break;}
       default:
          return state;
    }


    return state
}
export const addPostActionCreator = () => {

   return (
       {
          type: ADD_POST
       }
   )
}
export const addNewPostTextActionCreator = (text) => {
   return (
       {
          type: ADD_NEW_POST_TEXT,
          newText: text

       }
   )
}

export default contentReducer
