import React from 'react'
import Users from "./Users";
import {connect} from "react-redux";
import {showLastUsersActionCreator, showNextUsersActionCreator} from "../../Redux/users-reducer";


let mapStateToProps = (state) => {
    debugger
    return {
        users: state.users,
        pagingSize: state.pagingSize,
        startPosition: state.startPosition

    }
}

let mapDispatchToProps = (dispatch) => {
    return {
        showNextUsers: () => {dispatch(showNextUsersActionCreator())},
        showLastUsers: () => {dispatch(showLastUsersActionCreator())}
    }
}

const UsersContainer = connect(mapStateToProps, mapDispatchToProps)(Users)
export default UsersContainer
