import React from 'react'

class ProfileStatus extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            editMode:false
        }

    }

    onDoubleClickHandler() {

        this.state.editMode=true
    }

    render() {

        return(
            <div>
                {!this.state.editMode &&
                <div>
                    <span onDoubleClick={this.onDoubleClickHandler.bind(this)}>{this.props.status}</span>
                </div>
                }
                {this.state.editMode &&
                    <div>
                        <input value={this.props.status}/>
                    </div>
                }
            </div>
        )
    }


}

export default ProfileStatus
