import React from 'react'
import st from './Users.module.css'
import User from "./User";
import * as axios from 'axios'

const Users = (props) => {
 console.log(props)

    let getUsers = () => {
        if (props.users.length === 0) {
            axios.get("https://social-network.samuraijs.com/api/1.0/users")
                .then(response => {
                    let newArrUsers = []
                    response.data.items.map((item) => {

                        debugger
                        newArrUsers.push({
                            id: item.id,
                            name: item.name,
                            message: '',
                            birthday: 'n/a',
                            country: 'Russia',
                            city: 'Stp',
                            follow: item.followed
                        })
                    })
                    debugger
                    props.setUsers(newArrUsers)
                })
        }
    }
    let onClickNextHandler = () => {

        props.showNextUsers()
    }

    let onClickLastHandler = () => {

        props.showLastUsers()
    }
    let newUsers = props.users.slice(props.startPosition, props.startPosition + props.pagingSize)
    console.log(newUsers);
    return (
        <div className={st.users}>
            <button onClick={getUsers}>Get users</button>
            <div>
                <div className={st.whole_style}>
                    {(props.users.length === 0? 'Users are loading...' :<User users={newUsers} addFollow={props.addFollow}/>)}
                </div>
            </div>
            <div className={st.buttons}>
                <button onClick={onClickLastHandler}>Last</button>
                <button onClick={onClickNextHandler}>Next</button>
            </div>
        </div>

    )

}

export default Users
