import React from 'react'

class ProfileStatus extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            editMode:false,
            status: this.props.status
        }

    }


    onDoubleClickHandler = () => {

        this.setState({
            editMode: true
        })
    }

    onBlurClickHandler() {

        this.setState({
            editMode: false
        })

    }

    onChangeHandler(event) {
        console.log(event);
        debugger
        this.setState({
                status: event.current.value
            },

            this.props.updateStatus(this.state.status)
        )
    }

    render() {

        return(
            <div>
                {!this.state.editMode &&
                <div>
                    <span onDoubleClick={this.onDoubleClickHandler}>{'Status: ' + (!this.state.status? this.state.status: '')}</span>
                </div>
                }
                {this.state.editMode &&
                    <div>
                        <input autoFocus={true} onChange={(e) => {this.onChangeHandler(this.value).bind(this)}} onBlur={this.onBlurClickHandler.bind(this)} value={this.state.status}/>
                    </div>
                }
            </div>
        )
    }


}

export default ProfileStatus
