import * as axios from "axios";

const baseURL = 'https://social-network.samuraijs.com/api/1.0/'
const API_KEY = 'f96dbdbd-76d2-4f49-b850-bf9f7065d50b'

const instance = axios.create({
    withCredentials: true,
    headers: {"API-KEY": API_KEY}
})

export const getUsers = (pageNumber, pageSize) => {

    return instance.get(baseURL + `users?page=${pageNumber}&count=${pageSize}`).then(response => response.data)
}


export const getProfile = (userID) => {
    return instance.get(baseURL + `profile/${userID}`).then(response => {
        return response.data
    })
}

export const getUserData = () => {
    return instance.get(baseURL + `auth/me`).then(response => response.data)
}

export const setFollow = (id) => {
    return instance.post(baseURL + `follow/${id}`).then(response => response.data)
}

export const setUnfollow = (id) => {
    return instance.delete(baseURL + `follow/${id}`).then(response => response.data)

}
