       case ADD_POST:
          state.myPosts.push({id: '4', from: 'Elena', message: state.newPostText, date: '25.06.20', like: '0'})
          state.newPostText = ''
          break;
       case ADD_NEW_POST_TEXT:
          state.newPostText = action.newText
          break;
       default:
          return state;
    }


    return state
}
