import React from "react";
import classes from './MyPosts.module.css'
import Post from './Post/Post'

const MyPosts = (props) => {

    let addPostActionCreator = () => {
        return (
            {
                type:'ADD-POST'
            }
        )
    }

    let addNewPostTextActionCreator = (text) => {
        return (
            {
                type: 'ADD-NEW-POST-TEXT',
                newText: text

            }
        )
    }

    let newRef = React.createRef()

    let onClickHandler = () => {

        props.dispatch(addPostActionCreator())
        newRef.current.value = ''
    }

    let onChangeHandler = () => {

        props.dispatch(addNewPostTextActionCreator(newRef.current.value))
    }

    return (
        <div className={classes.item}>
            <h3>My posts</h3>
            <div className={classes.item}>
                <textarea onChange={onChangeHandler} id='newPost' ref={newRef} value={props.newPostText}/>
                <button onClick={onClickHandler}>Add post</button>
                <button onClick={ () => {console.log('Elena.Hello!')}}>Remove</button>
            </div>
            <div className={classes.item}>
            <Post myPosts={props.myPosts}/>
            </div>
        </div>
    )
}


export default MyPosts
