            birthday: '25.06.1983',
            country: 'Russia',
            city: 'Stp',
            follow: false
        }
    ],
    pagingSize: 4,
    startPosition: 0

}

const usersReducer = (state = initialState, action) => {


    switch (action.type) {
        case NEXT_USERS: {

            if (state.startPosition <= state.users.length) { return {
                ...state,
                startPosition: state.startPosition + state.pagingSize
            }} else {
                alert("Пользователей больше нет")
                return state
            }
        }
        case LAST_USERS: {
            if (state.startPosition > 0) {return {
                ...state,
                startPosition: state.startPosition - state.pagingSize
            }} else {
                alert("Это начало списка")
                return state
            }
        }
        case FOLLOW: {
           let copyUsers = [...state.users]
            copyUsers.map((item) => {
                if (item.id === action.id) {
                   item.follow = !item.follow
                }
            } )
            return {
               ...state,
                users: copyUsers
            }
        }
        default:
            return state;
    }


    return state
}
export const showNextUsersActionCreator = () => {

    return (
        {
            type: NEXT_USERS
        }
    )
}
export const showLastUsersActionCreator = () => {

    return (
        {
            type: LAST_USERS
        }
    )
}

export const addFollowActionCreator = (id) => {

    return (
        {
            type: FOLLOW,
            id: id
        }
    )
}


export default usersReducer
