const ADD_POST = 'ADD-POST';
const ADD_NEW_POST_TEXT = 'ADD-NEW-POST-TEXT'

let store = {
    _state: {
        profilePage: {
            allMessages: [
                {
                    id: '1',
                    from: 'Elena',
                    to: 'Alice',
                    message: 'Hello. how are you?',
                    date: '25.06.20'
                },
                {
                    id: '2',
                    from: 'Alice',
                    to: 'Elena',
                    message: 'Hello. I am fine. What about you?',
                    date: '25.06.20'
                },
                {
                    id: '3',
                    from: 'Elena',
                    to: 'Tamara',
                    message: 'Hello. how are you?',
                    date: '26.06.20'
                }
            ],

            myUsers: [
                {id: '1', name: 'Elena'},
                {id: '2', name: 'Alice'},
                {id: '3', name: 'Sergei'},
                {id: '4', name: 'Irina'},
                {id: '5', name: 'Tamara'}
            ]
        },

        myPosts: [
            {
                id: '1',
                from: 'Elena',
                message: 'It is first post',
                date: '25.06.20',
                like: '25'
            },
            {
                id: '2',
                from: 'Alice',
                message: 'Today is funny day',
                date: '25.06.20',
                like: '28'
            },
            {
                id: '3',
                from: 'Elena',
                message: 'Today is boring day',
                date: '26.06.20',
                like: '8'
            }
        ],
        sidebar: {},
        newPostText: 'enter here'

    },
    getState() {
      return this._state
    },
    dispatch(action) {
      if (action.type === 'ADD-POST')  {
         this._addPost()
      } else if (action.type === 'ADD-NEW-POST-TEXT') {
          this._addNewPostText(action.newText)
      }
    },
    rerenderTree() {
        console.log('state has been changed')
    },
    _addPost() {


        this._state.myPosts.push({id: '4', from: 'Elena', message: this._state.newPostText, date: '25.06.20', like: '0'})
        this.rerenderTree(this._state)
        this._state.newPostText = ''
    },
    _addNewPostText(newText) {

        this._state.newPostText = newText
        this.rerenderTree(this._state)
    },

    subscribe(observer) {
        this.rerenderTree = observer
    }

}

export const addPostActionCreator = () => {

    return (
        {
            type: ADD_POST
        }
    )
}

export const addNewPostTextActionCreator = (text) => {
    return (
        {
            type: ADD_NEW_POST_TEXT,
            newText: text

        }
    )
}

export default store
