import React from 'react'
import ReactDOM from 'react-dom'
import App from './app'
import {addPost, addNewPostText, subscribe} from "./Redux/State";
import store from './Redux/State'

export let rerenderTree = (state) => {
    ReactDOM.render(<App state={state} addPost={store.addPost.bind(store)} addNewPostText={store.addNewPostText.bind(store)}/>, document.getElementById('root'))
}




rerenderTree(store.getState())
store.subscribe(rerenderTree)
