let store = {
    _state: {
        profilePage: {
            allMessages: [
                {
                    id: '1',
                    from: 'Elena',
                    to: 'Alice',
                    message: 'Hello. how are you?',
                    date: '25.06.20'
                },
                {
                    id: '2',
                    from: 'Alice',
                    to: 'Elena',
                    message: 'Hello. I am fine. What about you?',
                    date: '25.06.20'
                },
                {
                    id: '3',
                    from: 'Elena',
                    to: 'Tamara',
                    message: 'Hello. how are you?',
                    date: '26.06.20'
                }
            ],

            myUsers: [
                {id: '1', name: 'Elena'},
                {id: '2', name: 'Alice'},
                {id: '3', name: 'Sergei'},
                {id: '4', name: 'Irina'},
                {id: '5', name: 'Tamara'}
            ]
        },

        myPosts: [
            {
                id: '1',
                from: 'Elena',
                message: 'It is first post',
                date: '25.06.20',
                like: '25'
            },
            {
                id: '2',
                from: 'Alice',
                message: 'Today is funny day',
                date: '25.06.20',
                like: '28'
            },
            {
                id: '3',
                from: 'Elena',
                message: 'Today is boring day',
                date: '26.06.20',
                like: '8'
            }
        ],
        sidebar: {},
        newPostText: 'enter here'

    },
    rerenderTree() {
        console.log('state has been changed')
    },
    addPost() {

        let newPost = {
            id: '4',
            from: 'Elena',
            message: State.newPostText,
            date: '25.06.20',
            like: 0
        }
        State.myPosts.push(newPost)
        rerenderTree(State)
        State.newPostText = ''
    },
    addNewPostText(newText) {

        State.newPostText = newText
        rerenderTree(State)
    },

    subscribe(observer) {
        rerenderTree = observer
    }

}

export default State
