import React from 'react'
import st from './User.module.css'
import {NavLink} from "react-router-dom";
import * as axios from "axios";
import {setFollow} from "../../API/api";

const User = (props) => {

    console.log(props);

    return (
        <div>
            {props.users.map((item) => {
                return (
                    <div key={item.id} className={st.item_row}>
                        <div className={st.item_column}>
                            <NavLink to={'/profile/' + item.id}>
                                <img className={st.avatarUser} onClick={() =>


                                    props.setCurrentUserID(item.id)}
                                     src={(item.url === null ? 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcT3nLvF-yfAyZEiTtHRIYlq1bXrtscjd-mUJA&usqp=CAU' : item.url)}
                                     alt='avatarUsers'/>
                            </NavLink>
                            <button value={item.id} onClick={() => {
                                if (!item.follow) {
                                    console.log(item.follow)
                                    setFollow(item.id).then(data => {
                                            if (data.resultCode === 0) {
                                                props.addFollow(item.id)
                                            } else {
                                                alert('Something was wrong')
                                            }
                                        })

                                } else {
                                    axios.delete(`https://social-network.samuraijs.com/api/1.0/follow/${item.id}`, {
                                        withCredentials: true,
                                        headers: {"API-KEY": 'f96dbdbd-76d2-4f49-b850-bf9f7065d50b'}
                                    })
                                        .then(response => {
                                            if (response.data.resultCode === 0) {
                                                props.addFollow(item.id)
                                            } else {
                                                alert('Something was wrong')
                                            }
                                        })
                                }

                            }}>
                                {(item.follow ? 'Unfollow' : 'Follow')}
                            </button>
                        </div>
                        <div>
                            <div className={st.item_column}>
                                {item.name}
                                <div className={st.item}>
                                    {item.message}<br/>
                                </div>
                            </div>

                        </div>
                    </div>
                )
            })}
        </div>
    )

}

export default User
