import React from 'react'
import st from './Dialogs.module.css'
import {NavLink} from "react-router-dom"
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import {addNewMessageTextActionCreator, addMessageActionCreator} from "../../../Redux/State";

const Dialogs = (props) => {

    let newRef = React.createRef()

    let onClickHandler = () => {

        props.dispatch(addMessageActionCreator())
        newRef.current.value = ''
    }

    let onChangeHandler = (e) => {

        props.dispatch(addNewMessageTextActionCreator(e.target.value))
    }


    return (
        <div className={st.dialogs}>
            <div className={st.dialogs_items}>
                {props.myUsers.map((element) => {
                    return (
                        <div key={element.id}>
                        <DialogItem name={element.name} id={element.id}/>
                        </div>
                    )
                })}

            </div>
            <div className={st.messages}>
                <textarea placeholder='enter here' onChange={onChangeHandler} id='newMessage' ref={newRef} value={props.newMessageText}/>
                <button onClick={onClickHandler}>Send</button>
                {props.allMessages.map((element)=> {
                    return (
                        <div key={element.id}>
                        <Message item={element.date +' From ' + element.from + ' to ' + element.to +' : ' + element.message}/>
                    </div>
                        )
                })}

            </div>
        </div>
    )
}
export default Dialogs
