import React from 'react'
import {Field, reduxForm} from "redux-form";
import {alphaNumeric, minLength1, renderField, requiredString} from "./Validation";
import ReCAPTCHA from "react-google-recaptcha";


class Login extends React.Component {

     submit = values => {
          // print the form values to the console
          console.log(values)
     }


     render() {

          return (
              <div>
              <LoginReduxForm onSubmit={this.submit}/>
              </div>
              )
     }

}

const LoginForm = (props) => {
     const { handleSubmit } = props
    let checkedCaptcha = false

    function onChangecaptcha(value) {
       return (checkedCaptcha = true)
    }

     return (
         <div>
              <p>LOGIN</p>
              <form onSubmit={handleSubmit}>
                   <div>
                        <Field
                            label={"Login"}
                            name={"login"}
                            type="text"
                            component={renderField}
                            validate={[requiredString, minLength1]}
                            warn={alphaNumeric}
                        />
                   </div>
                   <div>
                        <Field
                            label={"Password"}
                            name={"password"}
                            type="text"
                            component={renderField}
                            validate={[requiredString, minLength1]}
                            warn={alphaNumeric}
                        />
                   </div>
                   <div>
                        <Field
                            name={"rememberMe"}
                            type={"checkbox"}
                            component={renderField}
                        />remember me
                   </div>
                   <div>
                        <button disabled={!checkedCaptcha} type={"submit"}>Login</button>
                       <ReCAPTCHA sitekey='6Le4-a8ZAAAAAAdxK3R8n7ntfVnbLYJqwT87stUC' onChange={onChangecaptcha}/>
                   </div>
              </form>
         </div>

     )

}


const LoginReduxForm = reduxForm({
     // a unique name for the form
     form: 'LoginForm'
})(LoginForm)





export default Login
