import React from 'react'
import Content from "../Content";
import * as axios from "axios";
import {connect} from "react-redux";
import {addRequestedUserIdActionCreator, getUserInfoActionCreator} from "../../../Redux/content-reducer";
import Loader from "./../../Users/loader";

class ProfileClass extends React.Component {
    constructor(props) {
        super(props);
    }

    componentDidMount() {
        //this.props.setFetching(true)
        axios.get(`https://social-network.samuraijs.com/api/1.0/profile/2`)
            .then(response => {
                debugger
                this.props.getUserInfo(response.data)
                })

    }

    render() {
        return (
            <div>
                {this.props.userInfo === null? <Loader/>: <Content {...this.props}/>}
            </div>
        )

    }
}

let mapStateToProps = (state) => {
    console.log(state);
    return {
        userInfo: state.myPosts.userInfo,
        requestUserId: state.myPosts.requestUserId
    }
}

let mapDispatchToProps = (dispatch) => {
    return {
        getUserInfo: (userInfo) => {dispatch(getUserInfoActionCreator(userInfo))},
        addRequestedUserId: (id) => {dispatch(addRequestedUserIdActionCreator(id))},
         }
}
const ProfileContainer = connect(mapStateToProps,mapDispatchToProps)(ProfileClass)


export default ProfileContainer
