import React from 'react'
import st from './Users.module.css'
import User from "./User";
import * as axios from 'axios'

class UserClass extends React.Component  {

    constructor(props) {
        super(props);

    }

    getUsersFromServer = (pageNumber, pageSize) => {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${pageSize}`)
            .then(response => {

                let newArrUsers = []
                response.data.items.map((item) => {
                    newArrUsers.push({
                        id: item.id,
                        name: item.name,
                        message: (item.status === null? '': item.status),
                        url: item.photo.small,
                        follow: item.followed
                    })
                })
                debugger
                this.props.setUsers(newArrUsers, response.data.totalCount)
            })
    }

componentDidMount() {
    this.getUsersFromServer(this.props.currentPage,this.props.pagingSize)
}

    onPageChanged = (itemNumber) => {
        this.props.setCurrentPage(itemNumber)
        this.getUsersFromServer(itemNumber,this.props.pagingSize)
    }



    onClickNextHandler = (nextPage) => {

        this.props.showNextUsers()
        this.getUsersFromServer(nextPage,this.props.pagingSize)
    }

    onClickLastHandler = (lastPage) => {

        this.props.showLastUsers()
        this.getUsersFromServer(lastPage,this.props.pagingSize)
    }

    render() {
        let pageCount = Math.ceil(this.props.totalUsersCount/this.props.pagingSize)
        debugger
        let arrPages = []
        for (let i=1; i<= pageCount; i++) {
            arrPages.push(i)
        }
        //let newUsers = this.props.users.slice(this.props.startPosition, this.props.startPosition + this.props.pagingSize)
        return (
            <div className={st.users}>

                <div>
                    <div className={st.whole_style}>
                        {(this.props.users.length === 0? 'Users are loading...' :<User users={this.props.users} addFollow={this.props.addFollow}/>)}
                    </div>
                </div>
                <div className={st.buttons}>
                    <button onClick={() => {this.onClickLastHandler(this.props.currentPage-1)}}>Last</button>
                    <button onClick={() => {this.onClickNextHandler(this.props.currentPage+1)}}>Next</button>
                </div>
                <div>
                    {arrPages.map((itemNumber) => {
                        return (
                        <span onClick={() => {this.onPageChanged(itemNumber)}} className={itemNumber === this.props.currentPage && st.selectedPage}>{itemNumber} </span>
                    )})}
                </div>
            </div>

        )
    }


}

export default UserClass
