import React from 'react'
import st from './User.module.css'
import {NavLink} from "react-router-dom";
import * as axios from "axios";

const User = (props) => {

    console.log(props);

    return (
        <div>
            {props.users.map((item) => {
                return (
                    <div key={item.id} className={st.item_row}>
                        <div className={st.item_column}>
                            <NavLink to={'/profile/'+ item.id}>
                            <img className={st.avatarUser} onClick={() =>


                                props.setCurrentUserID(item.id)}
                                src={(item.url === null? 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcT3nLvF-yfAyZEiTtHRIYlq1bXrtscjd-mUJA&usqp=CAU' : item.url)}
                                alt='avatarUsers'/>
                            </NavLink>
                        <button value={item.id} onClick={() =>
                        {
                            let isFollow = false
                            axios.get(`https://social-network.samuraijs.com/api/1.0/follow/${item.id}`, {withCredentials: true})
                                .then(response => {
                                    isFollow = response.data
                                  if (isFollow) {

                                  } else {
                            axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${item.id}`, {withCredentials: true})
                                .then(response => {
                                    let info = response.data
                                    })}
                                })
                            props.addFollow(item.id)}}>
                            {(item.follow ? 'Unfollow' : 'Follow')}
                        </button>
                        </div>
                        <div>
                        <div className={st.item_column}>
                            {item.name}
                            <div className={st.item}>
                            {item.message}<br/>
                        </div>
                        </div>

                        </div>
                    </div>
            )})}
        </div>
    )

}

export default User
