import React from 'react'
import st from './User.module.css'

const User = (props) => {

    console.log(props);

    return (
        <div>
            {props.users.map((item) => {
                return (
                    <div key={item.id} className={st.item_row}>
                        <div className={st.item_column}>
                            <img className={st.avatarUser}
                                src='https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSOFZ5_ZOtn_3eMkSx94o0duB4R1yjrwJJ9yg&usqp=CAU'
                                alt='avatarUsers'/>
                        <button value={item.id} onClick={() => {props.addFollow(item.id)}}>{(item.follow ? 'Unfollow' : 'Follow')}</button>
                        </div>
                        <div>
                        <div className={st.item_column}>
                            {item.name}
                            <div className={st.item}>
                            {item.message}<br/>
                        </div>
                        </div>
                        <div className={st.item_column}>
                            {item.country + ','} <br/>
                            {item.city}
                        </div>
                        </div>
                    </div>
            )})}
        </div>
    )

}

export default User
