import React from 'react'
import st from './Users.module.css'
import User from "./User";
import * as axios from 'axios'

let Users = (props) => {
 console.log(props)


    return (
            <div className={st.users}>

                <div>
                    <div className={st.whole_style}>
                        {(props.userPage.users.length === 0? <p>'Users are loading...'</p> :<User users={props.userPage.users} addFollow={props.userPage.addFollow}/>)}
                    </div>
                </div>
                <div className={st.buttons}>
                    <button onClick={() => {props.onClickLastHandler(props.userPage.currentPage-1)}}>Last</button>
                    <button onClick={() => {props.onClickNextHandler(props.userPage.currentPage+1)}}>Next</button>
                </div>
                <div>
                    {props.arrPages.map((itemNumber) => {
                        return (
                            <span onClick={() => {props.onPageChanged(itemNumber)}} className={itemNumber === props.userPage.currentPage && st.selectedPage}>{itemNumber} </span>
                        )})}
                </div>
            </div>

    )

}

export default Users
