import React from 'react'
import st from './Users.module.css'
import User from "./User";
import * as axios from 'axios'

class UserClass extends React.Component  {

    constructor(props) {
        super(props);

    }

componentDidMount() {
    axios.get("https://social-network.samuraijs.com/api/1.0/users")
        .then(response => {
            debugger
            let newArrUsers = []
            response.data.items.map((item) => {

                debugger
                newArrUsers.push({
                    id: item.id,
                    name: item.name,
                    message: '',
                    birthday: 'n/a',
                    country: 'Russia',
                    city: 'Stp',
                    follow: item.followed
                })
            })
            this.props.setUsers(newArrUsers, response.data.totalUsersCount)
        })
}



    onClickNextHandler = () => {

        this.props.showNextUsers()
    }

    onClickLastHandler = () => {

        this.props.showLastUsers()
    }

    render() {
        let pageCount = Math.ceil(this.props.totalUsersCount/this.props.pagingSize)
        let arrPages = []
        for (let i=0; i<= pageCount; i++) {
            arrPages.push(i)
        }
        let newUsers = this.props.users.slice(this.props.startPosition, this.props.startPosition + this.props.pagingSize)
        return (
            <div className={st.users}>

                <div>
                    <div className={st.whole_style}>
                        {(this.props.users.length === 0? 'Users are loading...' :<User users={newUsers} addFollow={this.props.addFollow}/>)}
                    </div>
                </div>
                <div className={st.buttons}>
                    <button onClick={this.onClickLastHandler}>Last</button>
                    <button onClick={this.onClickNextHandler}>Next</button>
                </div>
                <div>
                    <span>{arrPages}</span>
                </div>
            </div>

        )
    }


}

export default UserClass
