            }} else {
                alert("Это начало списка")
                return state
            }
        }
        case FOLLOW: {
           let copyUsers = [...state.users]
            copyUsers.map((item) => {
                if (item.id === action.id) {
                   item.follow = !item.follow
                }
            } )
            return {
               ...state,
                users: copyUsers
            }
        }
        case SET_USERS: {
           let copyUsers = [...state.users, action.newUsers]
            return {
                ...state,
                users: copyUsers
            }
        }
        default:
            return state;
    }


    return state
}
export const showNextUsersActionCreator = () => {

