const SET_USER_DATA = 'SET-USER-DATA';


let initialState = {
    userID: null,
    email: null,
    login: null,
    isFetching: false,
    isAuth: false

}

const authReducer = (state = initialState, action) => {

    switch (action.type) {
        case SET_USER_DATA: {
            return {
                ...state,
                ...action.userData,
                isAuth: true
            }
            break
        }

        default:
            return state;
    }
}


export const setUserDataActionCreator = (userID, email, login) => {
    return (
        {
            type: SET_USER_DATA,
            userData: { userID, email, login}

        }
    )
}

export default authReducer
